#define ImportTypes $listbuild("INC", "INT", "CLS", "MAC", "MVI", "MVB", "BAS", "DFI", "CSR", "CSP") #define ImportTypeDescriptions $listbuild("include files", "intermediate routines", "classes", "macro routines", "MV Intermediate routines", "MV Basic routines", "Basic routines", "DFI XML", "CSP Rules", "files") #define RoutineExtensions $listbuild(".MAC", ".INT", ".INC", ".MVB", ".MVI", ".BAS", ".DFI") #define RoutinesGroups "MAC,INT,INC,MVB,MVI,BAS,DFI" #define IsRoutine(%type) ($$$RoutinesGroups[%type) #define GetTypeDescription(%type) $select($$$IsRoutine(%type)=1:$$$RoutineType,(%type = "CLS"):$$$ClassType,(%type = "PRJ"):$$$ProjectType,1:$$$FileType) #define GetGroupTypeDescription(%type) $case(%type,"MAC":$$$MACGroupType,"INC":$$$INCGroupType,"INT":$$$INTGroupType,"BAS":$$$BASGroupType,"MVB":$$$MVBGroupType,"MVI":$$$MVIGroupType,"DFI":$$$DFIGroupType,"CLS":$$$CLSGroupType,:$$$FileGroupType) #def1arg PERROR(%errorArgs) $$$ERROR($$$GeneralError, $$$FormatMsg("Port Errors", %errorArgs)) #def1arg FormatWizardMsg(%args) $$$FormatMsg("Port Configuration Wizard", %args) #define RemoveClassExtension(%iname) $select($piece(%iname, ".", $$$ucase(%iname), *) [ "CLS" : $piece(%iname, ".", 1, *-1), 1: %iname) #define Slash $select($$$isWINDOWS : "\", 1: "/") #define NFN(%path) ##class(%File).NormalizeFilename(%path)
